mapper.insert:

  SELECT SEQ_CHAT_ROOMS_ID.NEXTVAL FROM DUAL

INSERT INTO CHAT_ROOMS (
  ROOM_ID, BIZ_CODE_ID, ROOM_NAME, CREATED_AT, CAPACITY
) VALUES (
  #{roomId},
  #{bizCodeId},
  #{roomName},
  SYSTIMESTAMP,
  #{capacity}
)

mapper.select:
SELECT ROOM_ID, BIZ_CODE_ID, ROOM_NAME, CREATED_AT, CAPACITY
FROM CHAT_ROOMS
WHERE BIZ_CODE_ID = #{bizCodeId}
