mapper.select:
SELECT SURVEY_ID
FROM CS_SURVEY_QUESTION
WHERE QUESTION_ID = #{questionId}

SELECT OPTION_ID
FROM CS_SURVEY_OPTION
WHERE QUESTION_ID = #{questionId}
  AND OPTION_VALUE = #{optionValue}

SELECT * FROM ( SELECT S.SURVEY_ID, S.SURVEY_TITLE, S.SURVEY_DESCRIPTION, S.USE_YN, (SELECT COUNT(*) FROM CS_SURVEY_RESP R WHERE R.SURVEY_ID = S.SURVEY_ID) AS participantCount, ROW_NUMBER() OVER (ORDER BY S.SURVEY_ID DESC) AS rn FROM CS_SURVEY S
AND S.SURVEY_TITLE LIKE '%' || #{keyword} || '%'
AND S.USE_YN = #{useYn}
) WHERE rn BETWEEN #{start} AND #{end}

SELECT COUNT(*) FROM CS_SURVEY
AND SURVEY_TITLE LIKE '%' || #{keyword} || '%'
AND USE_YN = #{useYn}

mapper.update:
UPDATE CS_SURVEY
SET USE_YN = #{useYn}
WHERE SURVEY_ID = #{surveyId}

mapper.select:
SELECT RESPONSE_ID
FROM CS_SURVEY_RESP
WHERE SURVEY_ID = #{surveyId}
  AND USER_ID   = #{userId}
ORDER BY RESPONSED_AT DESC
    FETCH FIRST 1 ROWS ONLY

SELECT ANSWER_ID, RESPONSE_ID, QUESTION_ID, OPTION_ID
FROM CS_SURVEY_ANSWER
WHERE RESPONSE_ID = #{responseId}

SELECT RESPONSED_AT
FROM CS_SURVEY_RESP
WHERE RESPONSE_ID = #{responseId}
